const bar=document.getElementById("progressBar"),button=document.getElementById("redirectButton");function redirect(){try{window.location.href="idpms://"}catch(e){console.error(`The forwarding failed, error: ${e}`)}}document.addEventListener("DOMContentLoaded",()=>{bar.addEventListener("animationend",e=>{"fillBar"===e.animationName&&redirect()}),button.addEventListener("click",redirect)});